import React, { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {};
    this.fetchToday();
    this.fetchAvailability();
  }

  groupBy = function(key) {
  return this.reduce(function(groups, item) {
    const val = item[key];
    groups[val] = groups[val] || [];
    groups[val].push(item);
    return groups;
    }, {});
  };

  async fetchToday() {
    try {
      const res = await fetch("http://localhost:4433/today");
      const json = await res.groupBy('id').json();
      this.setState({today: json.today});
    } catch (e) {
      console.error("Failed to fetch 'today' data", e);
    }
  }

  async fetchAvailability() {
    try {
      const res = await fetch("http://localhost:4433/availability");
      const json = await res.json();
      this.setState({availability: json.availability});
    } catch (e) {
      console.error("Failed to fetch 'today' data", e);
    }
  }

  handleSubmit(event) {
    event.preventDefault();
    const data = new FormData(event.target);

    fetch('/api/form-submit-url', {
      method: 'POST',
      body: data,
     });
   }

  renderTableData() {
      return this.state.availability.map((advisor, key) => {
         const { id, opentimes } = advisor //destructuring
         const timeslist = opentimes.map(function(opentime){
            return <li>
                    <time dateTime={opentime} className="book-time">{opentime}</time>
                    <form id="time-form" onSubmit={this.handleSubmit.bind(this)}>
                      <button type="submit" value="Submit" className="book btn-small btn-primary">Book</button>
                    </form>
                  </li>
         })
         return (
            <tr key={id}>
               <td>{id}</td>
               <td>
               <ul className="list-unstyled">
                  {timeslist}
                </ul>
               </td>
            </tr>
         )
      })
   }

  render() {

    return (
      <div className="App container">
        <h1>Book Time with an Advisor</h1>

        {this.state.today && <span id="today">Today is {this.state.today}.</span>}

        <form id="name-form" className="col-md-6" onSubmit={this.handleSubmit.bind(this)}>
          <div className="form-group">
            <label htmlFor="name-field">Your Name</label>
            <input type="text" id="name-field" className="form-control" />
          </div>
        </form>


        <h2>Available Times</h2>
        <table className="advisors table">
          <thead>
            <tr>
              <th>Advisor ID</th>
              <th>Available Times</th>
            </tr>
          </thead>
            {this.state.availability &&
            <tbody>
              {this.renderTableData()}
            </tbody>
            }
        </table>

        <h2>Booked Times</h2>
        <table className="bookings table">
          <thead>
            <tr>
              <th>Advisor ID</th>
              <th>Student Name</th>
              <th>Date/Time</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>36232</td>
              <td>John Smith</td>
              <td>
                <time dateTime="2019-04-03T10:00:00-04:00">4/3/2019 10:00 am</time>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    );
  }
}

export default App;
